i=i+1;
p.push({q:n_temp[1],w:n1.lastIndex});
}
i_tmp=i;

while((n_temp=n3.exec(result))!=null){
//è·å–æ¯è¯çš„æ ‡é¢˜
n_sub=n_temp[1];
//åˆ¤æ–­VIP
if((n_sub.match(/span/))!=null){
n_sub=n_sub.match(/span>(.+)<\/span><spa/)[1];n_sub='ğŸ”’'+n_sub;
}
//ç»™æ¯è¯çš„æ ‡é¢˜æ·»åŠ å·æ ‡é¢˜
